/**
 * Legacy stub for jQuery.
 *
 * The actual jQuery library is provided by the Vite vendor bundle that
 * imports the Composer-installed package `components/jquery`.
 * This placeholder file keeps historical component configuration working
 * in debug mode without embedding the full library in the repository.
 */
(function () {
    if (typeof window === 'undefined') {
        return;
    }

    var jqueryInstance = window.jQuery || window.$;
    if (!jqueryInstance) {
        if (typeof console !== 'undefined' && typeof console.warn === 'function') {
            console.warn('jQuery vendor bundle was not loaded before the legacy stub.');
        }
        return;
    }

    window.jQuery = jqueryInstance;
    window.$ = jqueryInstance;
}());
